.invoice-details div {
    width: 48%; /* Adjust width as needed */
}
.invoice-header {
    text-align: center;
    margin-bottom: 40px;
    font-size: 20px;

}
.invoice-details {
    margin-bottom: 20px;
    display:flex;
}
.invoice-details .div{
    padding:20px;
}
.invoice-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;
}
.invoice-table th, .invoice-table td {
    border: 1px solid #ccc;
    padding: 8px;
    text-align: left;
}
